<template>
    <div>
       

    <b-col class="item"   >
       <b-card no-body class="overflow-hidden" style="max-width: 540px;">
    <b-row no-gutters>
      <b-col md="6">
    
        <b-card-img :src="imagem" class="rounded-0" alt="Categorias"></b-card-img>
      </b-col>
      <b-col md="6">
        <b-card-body :title="categoria.nome">
          <b-card-text>
             {{categoria.nome}}
          </b-card-text>
          <b-button
          @click="setProdutos(categoria.id)"
          >
            ver categoria
          </b-button>
        </b-card-body>
      </b-col>
    </b-row>
  </b-card>
    </b-col>
  
    </div>
</template>

<script>
 

export default {
    data(){
        return {
            imagem: ''
        }
    },
    props: {
        categoria: {
            type: Object,
            default: null
        }
    },
    methods: {
        getImage(){
         
            if(this.categoria.nome==='Hambúrgueres') this.imagem = 'https://img.itdg.com.br/tdg/images/blog/uploads/2017/07/shutterstock_259773713.jpg'
            else if(this.categoria.nome==='Refrigerantes') this.imagem = 'https://st3.depositphotos.com/1063437/19230/i/1600/depositphotos_192300154-stock-photo-bottles-of-global-soft-drink.jpg'
            else if(this.categoria.nome==='Sucos') this.imagem = 'data:image/jpeg;base64,'
              else if(this.categoria.nome==='Porções') this.imagem = 'https://img.stpu.com.br/?img=https://s3.amazonaws.com/pu-mgr/default/a0R0f00000zlruvEAA/5ba93a8fe4b0e8f03d8e354f.jpg&w=710&h=462'
              else this.imagem = 'https://picsum.photos/400/400/?image=20'
        },
        setProdutos(inteiro){
             this.$router.push(`/produtos/${inteiro}`);
        }
    },
    mounted(){
        this.getImage()
    }
}
</script>

<style >
 .item{
      padding: 20px;
    }
